import React, { useRef } from "react";
import "Card.css";

function Card({
  title,
  description,
  children,
  image,
  backgroundCircleColor = "linear-gradient(to right, rgba(245, 70, 66, 0.75), rgba(8, 83, 156, 0.75) )",
}) {
  const car = useRef(null);
  const cont = useRef(null);
  //Items
  const t = useRef(null);
  const s = useRef(null);
  const p = useRef(null);
  const d = useRef(null);

  const containerMouseMoved = (e) => {
    let xAxis = (window.innerWidth / 2 - e.pageX) / 25;
    let yAxis = (window.innerHeight / 2 - e.pageY) / 25;
    car.current.style.transform = `rotateY(${xAxis}deg) rotateX(${yAxis}deg)`;
  };

  const containerMouseEnter = (e) => {
    car.current.style.transition = "none";
    //Popout
    t.current.style.transform = "translateZ(150px)";
    s.current.style.transform = "translateZ(200px) rotateZ(-45deg)";
    d.current.style.transform = "translateZ(125px)";

    console.log(s.current.style.transform);
    p.current.style.transform = "translateZ(75px)";
  };

  const containerMouseLeave = (e) => {
    car.current.style.transition = "all 0.5s ease";
    car.current.style.transform = `rotateY(0deg) rotateX(0deg)`;
    //Popback
    t.current.style.transform = "translateZ(0px)";
    s.current.style.transform = "translateZ(0px) rotateZ(0deg)";

    d.current.style.transform = "translateZ(0px)";

    p.current.style.transform = "translateZ(0px)";
  };

  return (
    <>
      <div
        className="container"
        ref={cont}
        onMouseMove={containerMouseMoved}
        onMouseEnter={containerMouseEnter}
        onMouseLeave={containerMouseLeave}
      >
        <div className="card" ref={car}>
          <div className="sneaker">
            <div
              className="circle"
              style={{ background: backgroundCircleColor }}
            ></div>
            <img src={image} alt="adidas" ref={s} />
          </div>
          <div className="info">
            <h1 className="title" ref={t}>
              {title}
            </h1>
            <h3 ref={d}>{description}</h3>
          </div>

          <div className="purchase" ref={p}>
            {children}
          </div>
        </div>
      </div>
    </>
  );
}

export const DefaultButton = ({ children, action }) => {
  return (
    <div className="purchase">
      <button id="cardButton" onClick={action}>
        {children}
      </button>
    </div>
  );
};

export default Card;
